mapper.select:
select id,activity_id, sku_code,product_name, product_code, product_brand_code,product_brand_name, product_category_code ,product_category_name, price_tax,discount,reduce,actual_price,status,create_time, update_time,is_delete,create_by ,update_by,activity_scope FROM activity_product where 1=1 and is_delete=0
and activity_id = #{activityId}
limit #{beginIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

SELECT p.product_category_code, p.product_category_name FROM activity_product p LEFT JOIN activity a ON a.activity_id = p.activity_id where 1=1
AND a.activity_id = #{activityId,jdbcType=VARCHAR}
GROUP BY p.product_category_code
