mapper.select:
select id AS id, copartner_area_id as copartnerAreaId, copartner_area_name as copartnerAreaName, copartner_area_company as copartnerAreaCompany, copartner_area_level as copartnerAreaLevel, copartner_area_name_up as copartnerAreaNameUp from copartner_area_info where 1=1
and copartner_area_name like concat('%',#{copartnerAreaName,jdbcType=VARCHAR},'%')
and copartner_area_company like concat('%',#{copartnerAreaCompany,jdbcType=VARCHAR},'%')
and copartner_area_level = #{copartnerAreaLevel,jdbcType=INTEGER}
order by copartner_area_name LIMIT #{beginIndex,jdbcType=BIGINT}, #{pageSize,jdbcType=BIGINT}
