mapper.select:
select copartner_area_id as copartnerAreaId, person_id as personId, person_name as personName, REPLACE(GROUP_CONCAT(role_code),',','、') as roleCode, REPLACE(GROUP_CONCAT(role_name),',','、') as roleName from copartner_area_role where 1=1
and copartner_area_id = #{copartnerAreaId,jdbcType=VARCHAR}
and person_id = #{personId,jdbcType=VARCHAR}
limit 1

select copartner_area_id as copartnerAreaId, person_id as personId, person_name as personName, REPLACE(GROUP_CONCAT(role_code),',','、') as roleCode from copartner_area_role where 1=1
and copartner_area_id = #{copartnerAreaId,jdbcType=VARCHAR}
and person_id = #{personId,jdbcType=VARCHAR}
and role_type = #{roleType,jdbcType=TINYINT}
limit 1

select copartner_area_id as copartnerAreaId, person_id as personId, person_name as personName, role_code as roleCode, role_name as roleName from copartner_area_role where 1=1
and copartner_area_id = #{copartnerAreaId,jdbcType=VARCHAR}
and person_id = #{personId,jdbcType=VARCHAR}
and role_code = #{roleCode,jdbcType=VARCHAR}
