mapper.select:
select id as id, order_id as order_id, order_item_id as order_item_id, order_item_code as order_item_code, product_id as product_id, product_code as product_code, product_name as product_name, sku_code as sku_code, sku_name as sku_name, unit as unit, activity_id as activity_id, product_gift as product_gift, quantity as quantity, deliver_quantity as deliver_quantity, deliver_difference_reason as deliver_difference_reason, sign_quantity as sign_quantity, sign_difference_reason as sign_difference_reason, price as price, tax_purchase_price as tax_purchase_price, money as money, activity_money as activity_money, actual_money as actual_money, share_money as share_money, create_time as create_time, create_by_id as create_by_id, create_by_name as create_by_name, update_time as update_time, update_by_id as update_by_id, update_by_name as update_by_name, status as status from order_store_product_item where 1 = 1
and id = #{id}
and order_id = #{orderId}
and order_item_id = #{orderItemId}
and order_item_code = #{orderItemCode}
order by create_time desc
