mapper.select:
select id as id, order_id as orderId, order_status as orderStatus, operation_content as operationContent, create_time as createTime, create_by_id as createById, create_by_name as createByName, update_time as updateTime, update_by_id as updateById, update_by_name as updateByName, status as status from order_store_operation_log where 1 = 1
and id = #{id}
and order_id = #{orderId}
order by create_time desc
