mapper.select:
select id as id, operation_code as operationCode, operation_type as operationType, source_type as sourceType, operation_content as operationContent, remark as remark, use_status as useStatus, create_by_id as createById, create_by_name as createByName, update_by_id as updateById, update_by_name as updateByName, create_time as createTime, update_time as updateTime from operation_log where 1 = 1
and id = #{id}
and operation_code = #{operationCode}
and operation_type = #{operationType}
and source_type = #{sourceType}
and use_status = #{useStatus}
order by create_time desc,id desc
