mapper.select:
select id as id, refund_id as refundId, refund_code as refundCode, order_id as orderId, pay_id as payId, refund_fee as refundFee, refund_status as refundStatus, refund_type as refundType, create_time as createTime, create_by_id as createById, create_by_name as createByName, update_time as updateTime, update_by_id as updateById, update_by_name as updateByName, status as status from order_store_refund where 1 = 1
and id = #{id}
and refund_status = #{refundStatus}
and refund_id = #{refundId}
and refund_code = #{refundCode}
and order_id = #{orderId}
and pay_id = #{payId}
and refund_type = #{refundType}
and status = #{status}
order by create_time desc
