mapper.select:
select id, change_in_gift_quota as changeInGiftQuota, bill_code as billCode, bill_link as billLink, type, DATE_FORMAT( begin_time, '%Y-%m-%d %h:%i:%s' ) AS beginTime, DATE_FORMAT( finish_time, '%Y-%m-%d %h:%i:%s' ) AS finishTime, store_id as storeId, DATE_FORMAT( create_time, '%Y-%m-%d %h:%i:%s' ) AS createTime, create_by as createBy, DATE_FORMAT( update_time, '%Y-%m-%d %h:%i:%s' ) AS updateTime, update_by as updateBy from gift_quotas_use_detail where 1 = 1
and id = #{id}
and store_id = #{storeId}
order by create_time desc
