mapper.select:
select id, change_in_gift_quota, bill_code, bill_link, type, DATE_FORMAT( begin_time, '%Y-%m-%d %h:%i:%s' ) AS begin_time, DATE_FORMAT( finish_time, '%Y-%m-%d %h:%i:%s' ) AS finish_time, store_id, DATE_FORMAT( create_time, '%Y-%m-%d %h:%i:%s' ) AS create_time, create_by, DATE_FORMAT( update_time, '%Y-%m-%d %h:%i:%s' ) AS update_time, update_by from gift_quotas_use_detail where 1 = 1
and id = #{id}
and store_id = #{storeId}
order by create_time desc
