mapper.select:

         SELECT * FROM order_after_sale
		 WHERE 1=1

         and distributor_id = #{distributorId}


		 and after_sale_code like concat(#{afterSaleCode,jdbcType=VARCHAR},'%') 


		 and order_code like concat(#{orderCode,jdbcType=VARCHAR},'%') 


		 and member_phone like concat(#{memberPhone,jdbcType=VARCHAR},'%') 


         and create_time >= #{beginTime}


         and create_time <= #{endTime}


         and pay_type = #{payType}


         and after_sale_status = #{afterSaleStatus}


         and origin_type =#{originType}
