mapper.select:
select id, order_code orderCode, order_type orderType, order_status orderStatus, payment_status paymentStatus, payment_time paymentTime, payment_type paymentType, payment_type_code paymentTypeCode, store_id storeId, store_code storeCode, store_name storeName, store_type storeType, total_orders totalOrders, actual_amount_paid actualAmountPaid, activity_amount activityAmount, preferential_quota preferentialQuota, logistics_remission_ratio logisticsRemissionRatio, logistics_remission_type logisticsRemissionType, place_order_code placeOrderCode, place_order_time placeOrderTime, account_balance accountBalance, line_credit lineCredit, receiving_address receivingAddress, arrival_time arrivalTime, consignee, consignee_phone consigneePhone, company_code companyCode, company_name companyName, original, province_code provinceCode, province_name provinceName, city_code cityCode, city_name cityName, district_code districtCode, district_name districtName, invoice_title invoiceTitle, invoice_type invoiceType, order_original orderOriginal, product_num productNum, supplier_code supplierCode, supplier_name supplierName, transport_center_code transportCenterCode, transport_center_name transportCenterName, warehouse_code warehouseCode, warehouse_name warehouseName, weight, zip_code zipCode, remake, update_by updateBy, create_by createBy, update_time updateTime, create_time createTime, application_gifts applicationGifts, apply_coupons applyCoupons from order_list where 1=1
and (store_code LIKE CONCAT('%',#{storeCode}, '%') or store_name LIKE CONCAT('%',#{storeCode}, '%'))
and place_order_time>#{startPlaceOrderTime}
and #{endPlaceOrderTime}> place_order_time
and payment_status=#{paymentStatus}
and order_status=#{orderStatus}
and store_type=#{storeType}
LIMIT #{beginIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

    select
count(*)
  from order_list
  where 1=1

  and  (store_code  LIKE CONCAT('%',#{storeCode}, '%')
  or   store_name  LIKE CONCAT('%',#{storeCode}, '%'))


  and   place_order_time>#{startPlaceOrderTime}


  and  #{endPlaceOrderTime}> place_order_time


  and   payment_status=#{paymentStatus}


  and   order_status=#{orderStatus}


  and   store_type=#{storeType}

