mapper.select:

         SELECT * FROM order_info
         where 1=1

		 and order_id = #{orderId}


		 and order_code = #{orderCode}

		 ORDER BY create_time DESC

         SELECT order_id FROM order_info
         where 1=1
		 and order_code = #{orderCode,jdbcType=VARCHAR}

         SELECT a.* FROM order_info a
         left join order_pay b on a.order_id = b.order_id
		 WHERE 1=1

		 and a.order_id = #{orderId}


		 and a.order_code like concat('%',#{orderCode,jdbcType=VARCHAR},'%') 


		 and a.member_id =#{memberId}


		 and a.member_phone like concat('%',#{memberPhone,jdbcType=VARCHAR},'%') 


		 and (a.member_phone like concat('%',#{codeAndPhone,jdbcType=VARCHAR},'%') or a.order_code like concat('%',#{codeAndPhone,jdbcType=VARCHAR},'%'))


         and DATE_FORMAT(a.create_time,"%Y-%m-%d")  >= DATE_FORMAT(#{beginTime},"%Y-%m-%d")


         and DATE_FORMAT(a.create_time,"%Y-%m-%d") <= DATE_FORMAT(#{endTime},"%Y-%m-%d")


         and DATE_FORMAT(a.create_time,"%Y-%m-%d")  >= #{beginDate}


         and DATE_FORMAT(a.create_time,"%Y-%m-%d") <= #{endDate}


         and a.order_status = #{orderStatus}


         and a.origin_type =#{originType}


         and a.receive_type =#{receiveType}


         and a.receive_code =#{receiveCode}


         and a.distributor_id = #{distributorId}


		 and a.distributor_name like concat('%',#{distributorName,jdbcType=VARCHAR},'%')


         and b.pay_type = #{payType}

         ORDER BY a.create_time DESC

         limit #{beginIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

         SELECT count(*) FROM order_info a
         left join order_pay b on a.order_id = b.order_id
		 WHERE 1=1

		 and a.order_id = #{orderId}


		 and a.order_code like concat('%',#{orderCode,jdbcType=VARCHAR},'%') 


		 and a.member_id =#{memberId}


		 and a.member_phone like concat('%',#{memberPhone,jdbcType=VARCHAR},'%') 


		 and (a.member_phone like concat('%',#{codeAndPhone,jdbcType=VARCHAR},'%') or a.order_code like concat('%',#{codeAndPhone,jdbcType=VARCHAR},'%'))


         and DATE_FORMAT(a.create_time,"%Y-%m-%d")  >= DATE_FORMAT(#{beginTime},"%Y-%m-%d")


         and DATE_FORMAT(a.create_time,"%Y-%m-%d") <= DATE_FORMAT(#{endTime},"%Y-%m-%d")


         and DATE_FORMAT(a.create_time,"%Y-%m-%d")  >= #{beginDate}


         and DATE_FORMAT(a.create_time,"%Y-%m-%d") <= #{endDate}


         and a.order_status = #{orderStatus}


         and a.origin_type =#{originType}


         and a.receive_type =#{receiveType}


         and a.receive_code =#{receiveCode}


         and a.distributor_id = #{distributorId}


		 and a.distributor_name like concat('%',#{distributorName,jdbcType=VARCHAR},'%')


         and b.pay_type = #{payType}

SELECT SUM(b.actual_price) AS total_price FROM order_info b
WHERE  1=1
AND b.order_status NOT IN ('0','4')
AND b.pay_status='1'
and b.distributor_id =#{arg0}

and b.origin_type =#{arg1}

SELECT SUM(b.actual_price) AS total_price FROM order_info b
WHERE  1=1 
AND b.order_status NOT IN ('0','4')
AND b.pay_status='1'
and b.distributor_id = #{arg0}

AND b.origin_type =#{arg1}

and DATE_FORMAT(b.create_time,"%Y-%m") =#{arg2}

SELECT SUM(b.actual_price) AS total_price FROM order_info b
WHERE  1=1 
AND b.order_status NOT IN ('0','4')
AND b.pay_status='1'
and b.distributor_id = #{arg0}

and b.origin_type =#{arg1}

and DATE_FORMAT(b.create_time,"%Y-%m-%d") =#{arg2}

SELECT count(b.order_id) AS total_acount from order_info b
WHERE  
1=1
AND b.order_status NOT IN ('0','4')
AND b.pay_status='1'
and b.distributor_id = #{arg0}

and b.origin_type =#{arg1}

and DATE_FORMAT(b.create_time,"%Y-%m") =#{arg2}

SELECT count(b.order_id) AS total_acount from order_info b
WHERE  
1=1
AND b.order_status NOT IN ('0','4')
AND b.pay_status='1'
and b.distributor_id = #{arg0}

and b.origin_type =#{arg1}

and DATE_FORMAT(b.create_time,"%Y-%m-%d") =#{arg2}

SELECT CASE WHEN b.return_price IS NULL  THEN a.real_amt ELSE a.real_amt-b.return_price END AS real_amt FROM (
SELECT any_value(DATE_FORMAT(b.create_time,"%Y-%m")) AS create_time,SUM(actual_price) AS real_amt FROM order_info b
WHERE 1=1
AND b.order_status NOT IN ('0','4')
AND b.pay_status='1'
AND b.distributor_id = #{arg0}

and b.origin_type =#{arg1}

AND DATE_FORMAT(b.create_time,"%Y-%m") =#{arg2}
GROUP BY DATE_FORMAT(b.create_time,"%Y-%m")
)a
LEFT JOIN 
(
SELECT any_value(DATE_FORMAT(b.create_time,"%Y-%m")) AS create_time,SUM(return_price)AS return_price FROM order_after_sale b
WHERE 
1=1
AND b.after_sale_status='3'
AND b.distributor_id = #{arg0}

and b.origin_type =#{arg1}

AND DATE_FORMAT(b.create_time,"%Y-%m") =#{arg2}
GROUP BY DATE_FORMAT(b.create_time,"%Y-%m")
)b 
ON a.create_time = b.create_time

SELECT CASE WHEN b.return_price IS NULL  THEN a.real_amt ELSE a.real_amt-b.return_price END  AS real_amt FROM (
SELECT DATE_FORMAT(b.create_time,"%Y-%m-%d") AS create_time,COUNT(b.order_id) AS order_acount,SUM(actual_price) AS real_amt FROM order_info b
WHERE 1=1
AND b.order_status NOT IN ('0','4')
AND b.pay_status='1'
AND b.distributor_id = #{arg0}

and b.origin_type =#{arg1}

AND DATE_FORMAT(b.create_time,"%Y-%m-%d") = #{arg2}
GROUP BY DATE_FORMAT(b.create_time,"%Y-%m-%d")
)a
LEFT JOIN 
(
SELECT DATE_FORMAT(b.create_time,"%Y-%m-%d") AS create_time,SUM(return_price)AS return_price FROM order_after_sale b
WHERE 
1=1
AND b.after_sale_status='3'
AND b.distributor_id = #{arg0}

and b.origin_type =#{arg1}

AND DATE_FORMAT(b.create_time,"%Y-%m-%d") = #{arg2}
GROUP BY DATE_FORMAT(b.create_time,"%Y-%m-%d")
)b 
ON a.create_time = b.create_time

SELECT a.`create_time` as create_time ,a.order_acount as order_count,CASE WHEN b.return_price IS NULL  THEN a.real_amt ELSE a.real_amt-b.return_price END  AS actual_price FROM (
SELECT DATE_FORMAT(b.create_time,"%Y-%m-%d") AS create_time,COUNT(b.order_id) AS order_acount,SUM(actual_price) AS real_amt FROM order_info b
WHERE 1=1
AND b.order_status NOT IN ('0','4')
AND b.pay_status='1'
AND b.distributor_id = #{distributorId}

AND b.origin_type =#{originType}

AND DATE_FORMAT(b.create_time,"%Y-%m-%d") >= #{beginDate}
GROUP BY DATE_FORMAT(b.create_time,"%Y-%m-%d")
)a
LEFT JOIN 
(
SELECT DATE_FORMAT(b.create_time,"%Y-%m-%d") AS create_time,SUM(return_price)AS return_price FROM order_after_sale b
WHERE 
1=1
AND b.after_sale_status='3'
AND b.distributor_id = #{distributorId}

AND b.origin_type =#{originType}

AND DATE_FORMAT(b.create_time,"%Y-%m-%d") >= #{beginDate}
GROUP BY DATE_FORMAT(b.create_time,"%Y-%m-%d")
)b 
ON a.create_time = b.create_time

SELECT #{any} AS create_time ,a.order_acount AS order_count,CASE WHEN b.return_price IS NULL  THEN a.real_amt ELSE a.real_amt-b.return_price END  AS actual_price FROM (
  SELECT YEARWEEK(DATE_FORMAT(b.create_time,'%Y-%m-%d')) AS create_time,COUNT(b.order_id) AS order_acount,SUM(actual_price) AS real_amt FROM order_info b
  WHERE 1=1
  AND b.order_status NOT IN ('0','4')
  AND b.pay_status='1'
  AND b.distributor_id = #{distributorId}

  AND b.origin_type =#{originType}

  AND YEARWEEK(DATE_FORMAT(b.create_time,'%Y-%m-%d')) = YEARWEEK(NOW())-#{any}
  )a
  LEFT JOIN 
  (
  SELECT YEARWEEK(DATE_FORMAT(b.create_time,'%Y-%m-%d')) AS create_time,SUM(return_price)AS return_price FROM order_after_sale b
  WHERE 
  1=1
  AND b.after_sale_status='3'
  AND b.distributor_id = #{distributorId}

  AND b.origin_type =#{originType}

  AND YEARWEEK(DATE_FORMAT(b.create_time,'%Y-%m-%d')) = YEARWEEK(NOW())-#{any}
  )b 
  ON a.create_time = b.create_time

SELECT a.`create_time` AS create_time ,a.order_acount AS order_count,CASE WHEN b.return_price IS NULL  THEN a.real_amt ELSE a.real_amt-b.return_price END  AS actual_price FROM (
SELECT DATE_FORMAT(b.create_time,"%Y-%m") AS create_time,COUNT(b.order_id) AS order_acount,SUM(actual_price) AS real_amt FROM order_info b
WHERE 1=1
AND b.order_status NOT IN ('0','4')
AND b.pay_status='1'
AND b.distributor_id = #{distributorId}

AND b.origin_type =#{originType}

AND DATE_FORMAT(b.create_time,"%Y-%m") >= #{beginDate}
GROUP BY DATE_FORMAT(b.create_time,"%Y-%m")
)a
LEFT JOIN 
(
SELECT DATE_FORMAT(b.create_time,"%Y-%m") AS create_time,SUM(return_price)AS return_price FROM order_after_sale b
WHERE 
1=1
AND b.after_sale_status='3'
AND b.distributor_id = #{distributorId}

AND b.origin_type =#{originType}

AND DATE_FORMAT(b.create_time,"%Y-%m") >= #{beginDate}
GROUP BY DATE_FORMAT(b.create_time,"%Y-%m")
)b 
ON a.create_time = b.create_time

SELECT b.cashier_id,b.cashier_name, a.pay_type,SUM(pay_price) AS pay_price FROM order_info b LEFT JOIN order_pay a ON a.order_id = b.order_id WHERE 1=1 AND b.cashier_id =#{cashierId} AND b.order_status NOT IN (0,4) AND a.pay_type IN (3,4,5,6)
AND b.create_time >= #{beginDate}
AND b.create_time <= #{endDate}
GROUP BY a.pay_type,b.cashier_id,b.cashier_name ORDER BY b.create_time DESC

SELECT * FROM ( SELECT ROUND(b.actual_price*b.return_amount,2) AS return_price,COUNT(order_detail_id) AS return_order_amount FROM order_info a LEFT JOIN order_detail b ON a.order_id = b.order_id LEFT JOIN order_pay c ON a.order_id = c.order_id WHERE 1=1 AND a.cashier_id =#{cashierId} AND b.return_status = '3' AND c.pay_type IN (3,4,5,6)
and b.update_time >= #{beginDate}
and b.update_time <= #{endDate}
)a, (SELECT SUM(a.actual_price) AS sales_amount, COUNT(a.order_id) AS sales_order_amount FROM order_info a LEFT JOIN order_detail b ON a.order_id = b.order_id LEFT JOIN order_pay c ON a.order_id = c.order_id WHERE 1=1 AND a.cashier_id =#{cashierId} AND a.order_status NOT IN (0,4) AND c.pay_type IN (3,4,5,6)
and a.create_time >= #{beginDate}
and a.create_time <= #{endDate}
) b


         SELECT * FROM order_info a
         LEFT JOIN (SELECT order_id,SUM(IFNULL(return_price,0)) AS return_price  FROM order_after_sale GROUP BY order_id) b ON a.order_id = b.order_id
		 WHERE 1=1

		 and a.order_id = #{orderId}


		 and a.order_code = #{orderCode}


         and DATE_FORMAT(a.create_time,"%Y-%m-%d") = #{beginTime}


         and a.distributor_id = #{distributorId}


		     and a.order_status in (

			 '${order_status}'

			)

          and IFNULL(a.actual_price,0)-IFNULL(b.return_price,0) >0
          ORDER BY create_time DESC

         limit #{beginIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

         SELECT a.*,b.create_time as pay_time FROM order_info a
         left join order_pay b on a.order_id = b.order_id
		 WHERE 1=1

		 and a.order_id = #{orderId}


		 and a.order_code like concat('%',#{orderCode,jdbcType=VARCHAR},'%') 


		 and a.member_id =#{memberId}


		 and a.member_phone like concat('%',#{memberPhone,jdbcType=VARCHAR},'%') 


         and DATE_FORMAT(a.create_time,"%Y-%m-%d")  >= DATE_FORMAT(#{beginTime},"%Y-%m-%d")


         and DATE_FORMAT(a.create_time,"%Y-%m-%d") <= DATE_FORMAT(#{endTime},"%Y-%m-%d")


         and DATE_FORMAT(a.create_time,"%Y-%m-%d")  >= #{beginDate}


         and DATE_FORMAT(a.create_time,"%Y-%m-%d") <= #{endDate}


         and a.order_status = #{orderStatus}


         and a.origin_type =#{originType}


         and a.receive_type =#{receiveType}


         and a.receive_code =#{receiveCode}


         and a.distributor_id = #{distributorId}


         and b.pay_type = #{payType}

         ORDER BY a.create_time DESC

         limit #{beginIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

         SELECT COUNT(order_id) FROM order_info  
		 WHERE 1=1
		 and DATE_FORMAT(create_time,"%Y-%m-%d") >= #{beginDate}  
		 AND DATE_FORMAT(create_time,"%Y-%m-%d") <= #{endDate}

         and distributor_id = #{distributorId}

         SELECT COUNT(actual_price) FROM order_info  
		 WHERE 1=1
		 and DATE_FORMAT(create_time,"%Y-%m-%d") >= #{beginDate}  
		 AND DATE_FORMAT(create_time,"%Y-%m-%d") <= #{endDate}

         and distributor_id = #{distributorId}

         SELECT COUNT(DISTINCT member_id) FROM order_info  
		 WHERE 1=1
		 and DATE_FORMAT(create_time,"%Y-%m-%d") >= #{beginDate}  
		 AND DATE_FORMAT(create_time,"%Y-%m-%d") <= #{endDate}

         and distributor_id = #{distributorId}

         SELECT COUNT(order_id) FROM order_info  
		 WHERE 1=1

         and order_status = #{orderStatus}


         and distributor_id = #{distributorId}
