mapper.select:
select id as id, order_id as orderId, order_code as orderCode, order_status as orderStatus, return_status as returnStatus, pay_status as payStatus, order_type as orderType, franchisee_id as franchiseeId, store_id as storeId, store_name as storeName, total_price as totalPrice, actual_price as actualPrice, order_level as orderLevel, primary_code as primaryCode, create_time as createTime, create_by_id as createById, create_by_name as createByName, update_time as updateTime, update_by_id as updateById, update_by_name as updateByName from order_store_info where 1 = 1
and id = #{id}
and order_id = #{orderId}
and order_code = #{orderCode}
and order_status = #{orderStatus}
and pay_status = #{payStatus}
and order_type = #{orderType}
and store_id = #{storeId}
order by create_time desc

mapper.update:
update order_store_info set
order_status = #{orderStatus},
update_by_id = #{updateById}, update_by_name = #{updateByName}, update_time = sysdate() where id = #{id}

mapper.select:
select a1.id as id, a1.order_id as orderId, a1.order_code as orderCode, a1.order_status as orderStatus, a1.return_status as returnStatus, a1.pay_status as payStatus, a1.order_type as orderType, a1.franchisee_id as franchiseeId, a1.store_id as storeId, a1.store_name as storeName, a1.total_price as totalPrice, a1.actual_price as actualPrice, a1.order_level as orderLevel, a1.primary_code as primaryCode, a1.create_time as createTime, a1.create_by_id as createById, a1.create_by_name as createByName, a1.update_time as updateTime, a1.update_by_id as updateById, a1.update_by_name as updateByName from order_store_info a1 where 1 = 1
and a1.order_level = #{orderLevel}
order by a1.create_time desc

select a1.id as id, a1.order_id as orderId, a1.order_code as orderCode, a1.order_status as orderStatus, a1.return_status as returnStatus, a1.pay_status as payStatus, a1.order_type as orderType, a1.franchisee_id as franchiseeId, a1.store_id as storeId, a1.store_name as storeName, a1.total_price as totalPrice, a1.actual_price as actualPrice, a1.order_level as orderLevel, a1.primary_code as primaryCode, a1.create_time as createTime, a1.create_by_id as createById, a1.create_by_name as createByName, a1.update_time as updateTime, a1.update_by_id as updateById, a1.update_by_name as updateByName from order_store_info a1 where 1 = 1 and order_level = 2 and primary_code in (
'${primaryCode}'
) order by create_time desc
