mapper.select:
select id as id, order_id as orderId, order_code as orderCode, order_status as orderStatus, before_cancel_status as beforeCancelStatus, order_origin_type as orderOriginType, order_channel as orderChannel, supplier_code as supplierCode, supplier_name as supplierName, repertory_code as repertoryCode, repertory_name as repertoryName, return_status as returnStatus, pay_status as payStatus, order_type as orderType, franchisee_id as franchiseeId, store_id as storeId, store_name as storeName, total_price as totalPrice, actual_price as actualPrice, order_level as orderLevel, split_status as splitStatus, primary_code as primaryCode, goods_coupon as goodsCoupon, sending_id as sendingId, create_time as createTime, create_by_id as createById, create_by_name as createByName, update_time as updateTime, update_by_id as updateById, update_by_name as updateByName from order_store_info where 1 = 1
and id = #{id}
and order_id = #{orderId}
and order_code = #{orderCode}
and sending_id = #{sendingId}
and order_status = #{orderStatus}
and pay_status = #{payStatus}
and order_type = #{orderType}
and split_status = #{splitStatus}
and before_cancel_status = #{beforeCancelStatus}
and store_id = #{storeId}
order by create_time desc

select a1.id as id, a1.order_id as orderId, a1.order_code as orderCode, a1.order_status as orderStatus, a1.before_cancel_status as beforeCancelStatus, a1.order_origin_type as orderOriginType, a1.order_channel as orderChannel, a1.supplier_code as supplierCode, a1.supplier_name as supplierName, a1.repertory_code as repertoryCode, a1.repertory_name as repertoryName, a1.return_status as returnStatus, a1.pay_status as payStatus, a1.order_type as orderType, a1.franchisee_id as franchiseeId, a1.store_id as storeId, a1.store_name as storeName, a1.total_price as totalPrice, a1.actual_price as actualPrice, a1.order_level as orderLevel, a1.split_status as splitStatus, a1.primary_code as primaryCode, a1.goods_coupon as goodsCoupon, a1.sending_id as sendingId, a1.create_time as createTime, a1.create_by_id as createById, a1.create_by_name as createByName, a1.update_time as updateTime, a1.update_by_id as updateById, a1.update_by_name as updateByName from order_store_info a1 where 1 = 1
and a1.order_code like concat('%',#{orderCode,jdbcType=VARCHAR},'%')
and a1.store_name like concat('%',#{storeName,jdbcType=VARCHAR},'%')
and a1.order_status = #{orderStatus}
and a1.pay_status = #{payStatus}
and a1.order_type = #{orderType}
and date_format(a1.create_time,'%Y-%m-%d') >= #{createTimeBegin,jdbcType=VARCHAR}
and date_format(a1.create_time,'%Y-%m-%d') <= #{createTimeEnd,jdbcType=VARCHAR}
order by a1.create_time desc

select a1.id as id, a1.order_id as orderId, a1.order_code as orderCode, a1.order_status as orderStatus, a1.before_cancel_status as beforeCancelStatus, a1.order_origin_type as orderOriginType, a1.order_channel as orderChannel, a1.supplier_code as supplierCode, a1.supplier_name as supplierName, a1.repertory_code as repertoryCode, a1.repertory_name as repertoryName, a1.return_status as returnStatus, a1.pay_status as payStatus, a1.order_type as orderType, a1.franchisee_id as franchiseeId, a1.store_id as storeId, a1.store_name as storeName, a1.total_price as totalPrice, a1.actual_price as actualPrice, a1.order_level as orderLevel, a1.split_status as splitStatus, a1.primary_code as primaryCode, a1.goods_coupon as goodsCoupon, a1.sending_id as sendingId, a1.create_time as createTime, a1.create_by_id as createById, a1.create_by_name as createByName, a1.update_time as updateTime, a1.update_by_id as updateById, a1.update_by_name as updateByName from order_store_info a1 where 1 = 1 and order_level = 2 and primary_code in (
'${primaryCode}'
) order by create_time desc
