update.foreach:
update return_order_detail
set actual_return_product_count = #{item.actualReturnProductCount}
where return_order_code = #{item.returnOrderCode} and line_code=#{item.lineCode}

update return_order_detail
set actual_total_product_amount = #{item.actualTotalProductAmount}
where return_order_detail_id = #{item.returnOrderDetailId}

mapper.select:
select evidence_url
from return_order_detail
where return_order_detail_id = #{returnOrderDetailId}

select coalesce(sum(actual_total_product_amount),0) as amount from return_order_detail
where product_category_code LIKE concat('',#{categoryCode},'%')
and return_order_code in (
    select return_order_code from return_order_info


        and order_type=#{type}


        and return_reason_code=#{reasonCode}


)
