mapper.select:
SELECT customer_code as customerCode, customer_name as customerName, contact_person as contactPerson, phone_number as phoneNumber, identity_number as identityNumber, province_id as provinceId, province_name as provinceName, city_id as cityId, city_name as cityName, district_id as districtId, district_name as districtName, street_address as streetAddress, company_code as companyCode, company_name as companyName, use_status as useStatus, affiliated_company as affiliatedCompany, invite_people_code as invitePeopleCode, invite_people_name as invitePeopleName, create_by as createBy, update_by as updateBy, DATE_FORMAT( create_time, '%Y-%m-%d %h:%i:%s' ) createTime, DATE_FORMAT( update_time, '%Y-%m-%d %h:%i:%s' ) updateTime FROM wholesale_customers
1=1
and customer_code like CONCAT(CONCAT('%',#{customerCode}),'%')
and customer_name like CONCAT(CONCAT('%',#{customerName}),'%')
and phone_number like CONCAT(CONCAT('%',#{phoneNumber}),'%')
and province_id = #{provinceId}
and city_id = #{cityId}
and district_id = #{districtId}
ORDER BY create_time DESC
limit #{beginIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

SELECT COUNT(1) FROM wholesale_customers
1=1
and customer_code like CONCAT(CONCAT('%',#{customerCode}),'%')
and customer_name like CONCAT(CONCAT('%',#{customerName}),'%')
and phone_number like CONCAT(CONCAT('%',#{phoneNumber}),'%')
and province_id = #{provinceId}
and city_id = #{cityId}
and district_id = #{districtId}

SELECT customer_code as customerCode, warehouse_code as warehouseCode, sku_code as skuCode, sku_name as skuName, product_category_code as productCategroyCode, product_category_name as productCategoryName, product_brand_code as productBrandCode, product_brand_name as productBrandName, type FROM wholesale_rule
1=1
and customer_code = #{customerCode}
